mapper.select:
  		select p.pno,p.title,p.img1,m.nickname,c.person,c.me 
		from product p, member m, chatroom c 
		where c.person=m.id and p.pno=c.pno and c.me=#{id}

  		select p.pno,p.title,p.img1,m.nickname,c.person,c.me 
		from product p, member m, chatroom c 
		where c.person=m.id and p.pno=c.pno and c.me=#{id} and p.pno=#{pno}
